// You can reference the input variables using input.NAME
// Parsed JSON files could be referenced as fileInput
exports.step = function (input) {
	const mapObject = []
	input.metadataMap.id.forEach((value, index) => {
		mapObject.push(
			{
				"id": value,
				"options": [input.metadataMap.options[index]]
			},
		)
	})
	return mapObject
}

exports.step = function (input) {
  let fileList = input.fileList;
  const syncedAssets = input.syncedAssets;
  for (let i = 0; i < fileList.files.length; i++) {
    let obj = fileList.files[i];

    if (syncedAssets.indexOf(obj.id) !== -1) {
      fileList.files.splice(i, 1);
      i--;
    }
  }

  for (var i = 0; i < fileList.files.length; i++) {
    let obj = fileList.files[i];

    if (input.supportedTypes.includes(obj.extension) === false) {
        fileList.files.splice(i, 1);
        i--;
    }
  }
  return fileList;
};

